import React, { useState } from "react";
import signinimage from "../images/76884837.jpg";
import logo_web from "../images/logo_web.png";
function Header() {
	return <img src={logo_web} alt="Logo" />;
}

const Login = (props) => {
	const registerFormStyle = {
		marginLeft: '2cm',
		width: "500px",
	};
	const [email, setEmail] = useState("");
	const [password, setPassword] = useState("");
	const [error, setError] = useState(null);

	const handleSubmit = async (e) => {
		e.preventDefault();
		const signUpData = { email, password }
		const response = await fetch('/api/login', {
			method: 'POST',
			body: JSON.stringify(signUpData),
			headers: {
				'Content-Type': 'application/json'
			}
		})
		const json = await response.json()
		console.log(json)
		if (!response.ok) {
			alert(json.message)
			setError(json.error)
		}
		if (response.ok) {
			setError(null)
			window.alert('The login was successful')
			console.log('user login success')
			return
			
		}
	}

	return (
		<div className="body" style={registerFormStyle}>
			<div className="auth-form-container">
				<label htmlFor="Ebuy">Sign in here üîê</label>
				<div className="form-container">
					<form className="Login-form" onSubmit={handleSubmit}>
						<label htmlFor="email">Email/Phone</label>
						<input
							value={email}
							onChange={(e) => setEmail(e.target.value)}
							type="email"
							placeholder="your username"
							id="email"
							name="email"
						/>
						<label htmlFor="password">Password</label>
						<input
							value={password}
							onChange={(e) => setPassword(e.target.value)}
							type="password"
							placeholder="*********"
							id="password"
							name="password"
						/>
						<button type="submit" >Sign In</button>
						<button className="link-btn" onClick={() => props.onFormSwitch("register")}>
							Don't have an account? Register here
						</button>
					</form>
					<div className="image-container">
						<img
							src={signinimage}
							className="img-fluid"
							alt="Welcome to our eCommerce store!" />
						<button className="link-btn" onClick={() => props.onFormSwitch("register")}>

						</button>
					</div></div></div>
		</div>
	);
};

export default Login;
